import React, { Component } from 'react';
import { render } from 'react-dom';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import {Overlay} from 'react-bootstrap';
import {OverlayTrigger} from 'react-bootstrap';
import {Popover} from 'react-bootstrap';
import Link from 'next/link'
import Date from '../components/date'

const localizer = momentLocalizer(moment);

function Event({ event }) {
  let popoverClickRootClose = (
    <Popover id="popover-trigger-click-root-close" style={{ zIndex: 10000 }}>
      <strong>Holy guacamole!</strong> Check this info.
      <strong>{event.title}</strong>
    </Popover>
  );

  return (
    <div>
      <div>
        <OverlayTrigger id="help" trigger="click" rootClose container={this} placement="top" overlay={popoverClickRootClose}>
          <div>{event.title}</div>
        </OverlayTrigger>
      </div>
    </div>
  );
}

class Cal extends Component {
  constructor() {
    const now = new Date();
    const events = [
      {
          id: 1,
          title: 'Today',
          start: now,
          end: now,
          description: 'The date is: '
      },
      {
          id: 2,
          title: 'Blockchain 101',
          allDay: false,
          start: new Date(2020, 10, 15, 17, 0),
          end: new Date(2020, 10, 15, 18, 30),
          description: 'Introduction to Blockchain@IU and blockchain technology!'
      },
    ]
    this.state = {
      events
    };
  }

  render() {
    return (
      <div>
        <h2>Blockcain@IU Calendar</h2>
        <div style={{ height: '500pt'}}>
          <Calendar
            events={this.state.events}
            startAccessor="start"
            endAccessor="end"
            defaultDate={moment().toDate()}
            localizer={localizer}
             components={{
              event: Event
            }}
          />
        </div>
      </div>
    );
  }
}

export default Cal;
